1.0.0\n */\n\n/**\n * Table of Contents:\n * Theme Support\n * Required Files\n * Register Styles\n * Register Scripts\n * Register Menus\n * Custom Logo\n * WP Body Open\n * Register Sidebars\n * Enqueue Block Editor Assets\n * Enqueue Classic Editor Styles\n * Block Editor Settings\n */\n\n/**\n * Sets up theme defaults and registers support for various WordPress features.\n *\n * Note that this function is hooked into the after_setup_theme hook, which\n * runs before the init hook. The init hook is too late for some features, such\n * as indicating support for post thumbnails.\n */\nfunction twentytwenty_theme_support() {\n\n	// Add default posts and comments RSS feed links to head.\n	add_theme_support( \'automatic-feed-links\' );\n\n	// Custom background color.\n	add_theme_support(\n		\'custom-background\',\n		array(\n			\'default-color\' => \'f5efe0\',\n		)\n	);\n\n	// Set content-width.\n	global $content_width;\n	if ( ! isset( $content_width ) ) {\n		$content_width = 580;\n	}\n\n	/*\n	 * Enable support for Post Thumbnails on posts and pages.\n	 *\n	 * @link https://developer.wordpress.org/themes/functionality/featured-images-post-thumbnails/\n	 */\n	add_theme_support( \'post-thumbnails\' );\n\n	// Set post thumbnail size.\n	set_post_thumbnail_size( 1200, 9999 );\n\n	// Add custom image size used in Cover Template.\n	add_image_size( \'twentytwenty-fullscreen\', 1980, 9999 );\n\n	// Custom logo.\n	$logo_width  = 120;\n	$logo_height = 90;\n\n	// If the retina setting is active, double the recommended width and height.\n	if ( get_theme_mod( \'retina_logo\', false ) ) {\n		$logo_width  = floor( $logo_width * 2 );\n		$logo_height = floor( $logo_height * 2 );\n	}\n\n	add_theme_support(\n		\'custom-logo\',\n		array(\n			\'height\'      => $logo_height,\n			\'width\'       => $logo_width,\n			\'flex-height\' => true,\n			\'flex-width\'  => true,\n		)\n	);\n\n	/*\n	 * Let WordPress manage the document title.\n	 * By adding theme support, we declare that this theme does not use a\n	 * hard-coded <title> tag in the document head, and expect WordPress to\n	 * provide it for us.\n	 */\n	add_theme_support( \'title-tag\' );\n\n	/*\n	 * Switch default core markup for search form, comment form, and comments\n	 * to output valid HTML5.\n	 */\n	add_theme_support(\n		\'html5\',\n		array(\n			\'search-form\',\n			\'comment-form\',\n			\'comment-list\',\n			\'gallery\',\n			\'caption\',\n			\'script\',\n			\'style\',\n		)\n	);\n\n	/*\n	 * Make theme available for translation.\n	 * Translations can be filed in the /languages/ directory.\n	 * If you\'re building a theme based on Twenty Twenty, use a find and replace\n	 * to change \'twentytwenty\' to the name of your theme in all the template files.\n	 */\n	load_theme_textdomain( \'twentytwenty\' );\n\n	// Add support for full and wide align images.\n	add_theme_support( \'align-wide\' );\n\n	/*\n	 * Adds starter content to highlight the theme on fresh sites.\n	 * This is done conditionally to avoid loading the starter content on every\n	 * page load, as it is a one-off operation only needed once in the customizer.\n	 */\n	if ( is_customize_preview() ) {\n		require get_template_directory() . \'/inc/starter-content.php\';\n		add_theme_support( \'starter-content\', twentytwenty_get_starter_content() );\n	}\n\n	// Add theme support for selective refresh for widgets.\n	add_theme_support( \'customize-selective-refresh-widgets\' );\n\n	/*\n	 * Adds `async` and `defer` support for scripts registered or enqueued\n	 * by the theme.\n	 */\n	$loader = new TwentyTwenty_Script_Loader();\n	add_filter( \'script_loader_tag\', array( $loader, \'filter_script_loader_tag\' ), 10, 2 );\n\n}\n\nadd_action( \'after_setup_theme\', \'twentytwenty_theme_support\' );\n\n/**\n * REQUIRED FILES\n * Include required files.\n */\nrequire get_template_directory() . \'/inc/template-tags.php\';\n\n// Handle SVG icons.\nrequire get_template_directory() . \'/classes/class-twentytwenty-svg-icons.php\';\nrequire get_template_directory() . \'/inc/svg-icons.php\';\n\n// Handle Customizer settings.\nrequire get_template_directory() . \'/classes/class-twentytwenty-customize.php\';\n\n// Require Separator Control class.\nrequire get_template_directory() . \'/classes/class-twentytwenty-separator-control.php\';\n\n// Custom comment walker.\nrequire get_template_directory() . \'/classes/class-twentytwenty-walker-comment.php\';\n\n// Custom page walker.\nrequire get_template_directory() . \'/classes/class-twentytwenty-walker-page.php\';\n\n// Custom script loader class.\nrequire get_template_directory() . \'/classes/class-twentytwenty-script-loader.php\';\n\n// Non-latin language handling.\nrequire get_template_directory() . \'/classes/class-twentytwenty-non-latin-languages.php\';\n\n// Custom CSS.\nrequire get_template_directory() . \'/inc/custom-css.php\';\n\n/**\n * Register and Enqueue Styles.\n */\nfunction twentytwenty_register_styles() {\n\n	$theme_version = wp_get_theme()->get( \'Version\' );\n\n	wp_enqueue_style( \'twentytwenty-style\', get_stylesheet_uri(), array(), $theme_version );\n	wp_style_add_data( \'twentytwenty-style\', \'rtl\', \'replace\' );\n\n	// Add output of Customizer settings as inline style.\n	wp_add_inline_style( \'twentytwenty-style\', twentytwenty_get_customizer_css( \'front-end\' ) );\n\n	// Add print CSS.\n	wp_enqueue_style( \'twentytwenty-print-style\', get_template_directory_uri() . \'/print.css\', null, $theme_version, \'print\' );\n\n}\n\nadd_action( \'wp_enqueue_scripts\', \'twentytwenty_register_styles\' );\n\n/**\n * Register and Enqueue Scripts.\n */\nfunction twentytwenty_register_scripts() {\n\n	$theme_version = wp_get_theme()->get( \'Version\' );\n\n	if ( ( ! is_admin() ) && is_singular() && comments_open() && get_option( \'thread_comments\' ) ) {\n		wp_enqueue_script( \'comment-reply\' );\n	}\n\n	wp_enqueue_script( \'twentytwenty-js\', get_template_directory_uri() . \'/assets/js/index.js\', array(), $theme_version, false );\n	wp_script_add_data( \'twentytwenty-js\', \'async\', true );\n\n}\n\nadd_action( \'wp_enqueue_scripts\', \'twentytwenty_register_scripts\' );\n\n/**\n * Fix skip link focus in IE11.\n *\n * This does not enqueue the script because it is tiny and because it is only for IE11,\n * thus it does not warrant having an entire dedicated blocking script being loaded.\n *\n * @link https://git.io/vWdr2\n */\nfunction twentytwenty_skip_link_focus_fix() {\n	// The following is minified via `terser --compress --mangle -- assets/js/skip-link-focus-fix.js`.\n	?>\n	<script>\n	/(trident|msie)/i.test(navigator.userAgent)&&document.getElementById&&window.addEventListener&&window.addEventListener(\"hashchange\",function(){var t,e=location.hash.substring(1);/^[A-z0-9_-]+$/.test(e)&&(t=document.getElementById(e))&&(/^(?:a|select|input|button|textarea)$/i.test(t.tagName)||(t.tabIndex=-1),t.focus())},!1);\n	</script>\n	<?php\n}\nadd_action( \'wp_print_footer_scripts\', \'twentytwenty_skip_link_focus_fix\' );\n\n/** Enqueue non-latin language styles\n *\n * @since 1.0.0\n *\n * @return void\n */\nfunction twentytwenty_non_latin_languages() {\n	$custom_css = TwentyTwenty_Non_Latin_Languages::get_non_latin_css( \'front-end\' );\n\n	if ( $custom_css ) {\n		wp_add_inline_style( \'twentytwenty-style\', $custom_css );\n	}\n}\n\nadd_action( \'wp_enqueue_scripts\', \'twentytwenty_non_latin_languages\' );\n\n/**\n * Register navigation menus uses wp_nav_menu in five places.\n */\nfunction twentytwenty_menus() {\n\n	$locations = array(\n		\'primary\'  => __( \'Desktop Horizontal Menu\', \'twentytwenty\' ),\n		\'expanded\' => __( \'Desktop Expanded Menu\', \'twentytwenty\' ),\n		\'mobile\'   => __( \'Mobile Menu\', \'twentytwenty\' ),\n		\'footer\'   => __( \'Footer Menu\', \'twentytwenty\' ),\n		\'social\'   => __( \'Social Menu\', \'twentytwenty\' ),\n	);\n\n	register_nav_menus( $locations );\n}\n\nadd_action( \'init\', \'twentytwenty_menus\' );\n\n/**\n * Get the information about the logo.\n *\n * @param string $html The HTML output from get_custom_logo (core function).\n *\n * @return string $html\n */\nfunction twentytwenty_get_custom_logo( $html ) {\n\n	$logo_id = get_theme_mod( \'custom_logo\' );\n\n	if ( ! $logo_id ) {\n		return $html;\n	}\n\n	$logo = wp_get_attachment_image_src( $logo_id, \'full\' );\n\n	if ( $logo ) {\n		// For clarity.\n		$logo_width  = esc_attr( $logo[1] );\n		$logo_height = esc_attr( $logo[2] );\n\n		// If the retina logo setting is active, reduce the width/height by half.\n		if ( get_theme_mod( \'retina_logo\', false ) ) {\n			$logo_width  = floor( $logo_width / 2 );\n			$logo_height = floor( $logo_height / 2 );\n\n			$search = array(\n				\'/width=\\\"\\d+\\\"/iU\',\n				\'/height=\\\"\\d+\\\"/iU\',\n			);\n\n			$replace = array(\n				\"width=\\\"{$logo_width}\\\"\",\n				\"height=\\\"{$logo_height}\\\"\",\n			);\n\n			// Add a style attribute with the height, or append the height to the style attribute if the style attribute already exists.\n			if ( strpos( $html, \' style=\' ) === false ) {\n				$search[]  = \'/(src=)/\';\n				$replace[] = \"style=\\\"height: {$logo_height}px;\\\" src=\";\n			} else {\n				$search[]  = \'/(style=\"[^\"]*)/\';\n				$replace[] = \"$1 height: {$logo_height}px;\";\n			}\n\n			$html = preg_replace( $search, $replace, $html );\n\n		}\n	}\n\n	return $html;\n\n}\n\nadd_filter( \'get_custom_logo\', \'twentytwenty_get_custom_logo\' );\n\nif ( ! function_exists( \'wp_body_open\' ) ) {\n\n	/**\n	 * Shim for wp_body_open, ensuring backwards compatibility with versions of WordPress older than 5.2.\n	 */\n	function wp_body_open() {\n		do_action( \'wp_body_open\' );\n	}\n}\n\n/**\n * Include a skip to content link at the top of the page so that users can bypass the menu.\n */\nfunction twentytwenty_skip_link() {\n	echo \'<a class=\"skip-link screen-reader-text\" href=\"#site-content\">\' . __( \'Skip to the content\', \'twentytwenty\' ) . \'</a>\';\n}\n\nadd_action( \'wp_body_open\', \'twentytwenty_skip_link\', 5 );\n\n/**\n * Register widget areas.\n *\n * @link https://developer.wordpress.org/themes/functionality/sidebars/#registering-a-sidebar\n */\nfunction twentytwenty_sidebar_registration() {\n\n	// Arguments used in all register_sidebar() calls.\n	$shared_args = array(\n		\'before_title\'  => \'<h2 class=\"widget-title subheading heading-size-3\">\',\n		\'after_title\'   => \'</h2>\',\n		\'before_widget\' => \'<div class=\"widget %2$s\"><div class=\"widget-content\">\',\n		\'after_widget\'  => \'</div></div>\',\n	);\n\n	// Footer #1.\n	register_sidebar(\n		array_merge(\n			$shared_args,\n			array(\n				\'name\'        => __( \'Footer #1\', \'twentytwenty\' ),\n				\'id\'          => \'sidebar-1\',\n				\'description\' => __( \'Widgets in this area will be displayed in the first column in the footer.\', \'twentytwenty\' ),\n			)\n		)\n	);\n\n	// Footer #2.\n	register_sidebar(\n		array_merge(\n			$shared_args,\n			array(\n				\'name\'        => __( \'Footer #2\', \'twentytwenty\' ),\n				\'id\'          => \'sidebar-2\',\n				\'description\' => __( \'Widgets in this area will be displayed in the second column in the footer.\', \'twentytwenty\' ),\n			)\n		)\n	);\n\n}\n\nadd_action( \'widgets_init\', \'twentytwenty_sidebar_registration\' );\n\n/**\n * Enqueue supplemental block editor styles.\n */\nfunction twentytwenty_block_editor_styles() {\n\n	$css_dependencies = array();\n\n	// Enqueue the editor styles.\n	wp_enqueue_style( \'twentytwenty-block-editor-styles\', get_theme_file_uri( \'/assets/css/editor-style-block.css\' ), $css_dependencies, wp_get_theme()->get( \'Version\' ), \'all\' );\n	wp_style_add_data( \'twentytwenty-block-editor-styles\', \'rtl\', \'replace\' );\n\n	// Add inline style from the Customizer.\n	wp_add_inline_style( \'twentytwenty-block-editor-styles\', twentytwenty_get_customizer_css( \'block-editor\' ) );\n\n	// Add inline style for non-latin fonts.\n	wp_add_inline_style( \'twentytwenty-block-editor-styles\', TwentyTwenty_Non_Latin_Languages::get_non_latin_css( \'block-edito