5;\n	margin: 4rem 0;\n	overflow: auto;\n	padding: 3rem 2rem;\n	text-align: left;\n}\n\npre code {\n	background: transparent;\n	padding: 0;\n}\n\n\n/* Media ------------------------------------- */\n\nfigure {\n	display: block;\n	margin: 0;\n}\n\niframe {\n	display: block;\n	max-width: 100%;\n}\n\nvideo {\n	display: block;\n}\n\nsvg,\nimg,\nembed,\nobject {\n	display: block;\n	height: auto;\n	max-width: 100%;\n}\n\nfigcaption,\n.wp-caption-text {\n	color: #6d6d6d;\n	display: block;\n	font-size: 1.5rem;\n	font-weight: 500;\n	line-height: 1.2;\n	margin-top: 1.5rem;\n}\n\nfigcaption a,\n.wp-caption-text a {\n	color: inherit;\n}\n\n\n/* GALLERIES */\n\n.gallery {\n	display: flex;\n	flex-wrap: wrap;\n	margin: 3em 0 3em -0.8em;\n	width: calc(100% + 1.6em);\n}\n\n.gallery-item {\n	margin: 0.8em 0;\n	padding: 0 0.8em;\n	width: 100%;\n}\n\n.gallery-caption {\n	display: block;\n	margin-top: 0.8em;\n}\n\n\n/* Inputs ------------------------------------ */\n\nfieldset {\n	border: 0.2rem solid #dcd7ca;\n	padding: 2rem;\n}\n\nlegend {\n	font-size: 0.85em;\n	font-weight: 700;\n	padding: 0 1rem;\n}\n\nlabel {\n	display: block;\n	font-size: 1.6rem;\n	font-weight: 400;\n	margin: 0 0 0.5rem 0;\n}\n\nlabel.inline,\ninput[type=\"checkbox\"] + label {\n	display: inline;\n	font-weight: 400;\n	margin-left: 0.5rem;\n}\n\ninput,\ntextarea,\nbutton,\n.button,\n.faux-button,\n.wp-block-button__link,\n.wp-block-file__button {\n	font-family: -apple-system, BlinkMacSystemFont, \"Helvetica Neue\", Helvetica, sans-serif;\n	line-height: 1;\n}\n\n@supports ( font-variation-settings: normal ) {\n\n	input,\n	textarea,\n	button,\n	.button,\n	.faux-button,\n	.faux-button.more-link,\n	.wp-block-button__link,\n	.wp-block-file__button {\n		font-family: \"Inter var\", -apple-system, BlinkMacSystemFont, \"Helvetica Neue\", Helvetica, sans-serif;\n	}\n}\n\ninput,\ntextarea {\n	border-color: #dcd7ca;\n	color: #000;\n}\n\ncode,\ninput[type=\"url\"],\ninput[type=\"email\"],\ninput[type=\"tel\"] {\n\n	/*rtl:ignore*